import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { getMedicamentByName, getMedicaments } from 'src/firebaseConfig';

interface MedicamentDD {
  class: string;
  conditionnement: string;
  conservation: string;
  amm: string;
  dateamm: string;
  dci: string;
  dosage: string;
  forme: string;
  indication: string;
  laboratoire: string;
  sousclass: string;
  specialite: string;
  specification: string;
  tableau: string;
}

@Component({
  selector: 'app-recherche-medicament',
  templateUrl: './recherche-medicament.page.html',
  styleUrls: ['./recherche-medicament.page.scss'],
})
export class RechercheMedicamentPage implements OnInit {

  medicaments: MedicamentDD[] = [];
  nom: string = '';
  filteredMedicament: MedicamentDD | undefined;

  constructor(private firestore: AngularFirestore,private http: HttpClient) { }

  ngOnInit() {
    this.getMedicament();
  }

  async fetchMedicament() {
    try {
      if (this.nom) {
        // Filter the medicaments array based on the given name
        this.filteredMedicament = this.medicaments.find(med => med.dci.toLowerCase() === this.nom.toLowerCase());
        if (this.filteredMedicament) {
          console.log('Médecin disponible :', this.filteredMedicament);
        } else {
          console.error('Aucun médecament trouvé avec le nom spécifié.');
        }
      } else {
        console.error('Paramètres de recherche non valides.');
      }
    } catch (error) {
      console.error('Erreur lors de la récupération du médecin (fetchMedicament):', error);
    }
  }

  async getMedicament(){
    const medicaments =await getMedicaments(this.firestore);
    console.log(medicaments)

    this.medicaments=medicaments;
    console.log(this.medicaments)
  }

  readJsonFile(filePath: string): Promise<any> {
    return new Promise((resolve, reject) => {
      this.http.get(filePath, { responseType: 'json' })
        .toPromise()
        .then(response => {
          resolve(response);
        })
        .catch(error => {
          reject(error);
        });
    });
  }

  // Usage
  async readMedicamentsJson() {
    try {
      const medicaments = await this.readJsonFile('../../assets/medicaments.json');
      this.medicaments=medicaments;
      console.log(medicaments);
    } catch (error) {
      console.error('Error reading JSON file:', error);
    }
  }
}
