import {  CommonModule } from '@angular/common';
import { Component, Input, NgModule, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { FormsModule } from '@angular/forms';
import { IonicModule, ModalController } from '@ionic/angular';

interface RendezVousData {
  date: string;
  heure: string;
  nom: string;
  prenom: string;
  useruid: string;
  medecinNom?: string; // Ajout de la propriété medecinNom comme optionnelle
  medecinPrenom?: string; 
  medecinuid:string;// Ajout de la propriété medecinPrenom comme optionnelle
  key:string ;
  excuse?:string;
  statut?:string;
}
@Component({
  selector: 'app-fiche-patient-modal',
  templateUrl: './fiche-patient-modal.component.html',
  styleUrls: ['./fiche-patient-modal.component.scss'],
})
export class FichePatientModalComponent implements OnInit {
  @Input() rendezVousData!: RendezVousData; // Propriété d'entrée pour recevoir les données du rendez-vous

  fichePatient = {
    nom: '',
    prenom: '',
    age: null,
    adresse: '',
    telephone: ''
    // Ajoutez d'autres champs selon vos besoins
  };

  constructor(private modalController: ModalController) {
    // Initialisez rendezVousData ici si nécessaire
    this.rendezVousData = {} as RendezVousData;
  }

  ngOnInit() {}

  closeModal() {
    this.modalController.dismiss(); // Fermer le modal
  }

  saveFichePatient() {
    // Ajoutez ici le code pour sauvegarder les données de la fiche patient
    // Vous pouvez utiliser this.fichePatient pour accéder aux données
    console.log('Données de la fiche patient à sauvegarder :', this.fichePatient);
    this.modalController.dismiss({ data: this.fichePatient }); // Renvoyer des données au composant principal
  }

}

@NgModule({
  declarations: [FichePatientModalComponent],
  imports: [
    CommonModule,
    FormsModule,
    IonicModule
  ],
  providers: [AngularFirestore],
})
export class FichePatientModalModule {}

<ion-header>
  <ion-toolbar>
    <ion-title>Fiche Patient</ion-title>
    <ion-buttons slot="end">
      <ion-button (click)="closeModal()">Fermer</ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-card>
    <ion-card-header>
      <ion-card-title>{{ rendezVousData.nom }} {{ rendezVousData.prenom }}</ion-card-title>
    </ion-card-header>

    <ion-card-content>
      <!-- Champs du rendez-vous -->
      <ion-item>
        <ion-label>Nom</ion-label>
        <ion-input [(ngModel)]="rendezVousData.nom" disabled></ion-input>
      </ion-item>

      <ion-item>
        <ion-label>Prénom</ion-label>
        <ion-input [(ngModel)]="rendezVousData.prenom" disabled></ion-input>
      </ion-item>

      <!-- Champs classiques d'une fiche patient -->
      <ion-item>
        <ion-label>Âge</ion-label>
        <ion-input type="number" [(ngModel)]="fichePatient.age"></ion-input>
      </ion-item>

      <ion-item>
        <ion-label>Adresse</ion-label>
        <ion-input [(ngModel)]="fichePatient.adresse"></ion-input>
      </ion-item>

      <ion-item>
        <ion-label>Téléphone</ion-label>
        <ion-input type="tel" [(ngModel)]="fichePatient.telephone"></ion-input>
      </ion-item>

      <!-- Ajoutez d'autres champs selon vos besoins -->

      <ion-button expand="block" (click)="saveFichePatient()">Enregistrer</ion-button>
    </ion-card-content>
  </ion-card>
</ion-content>
